body:
* { scroll-behavior: smooth; scrollbar-width: thin; scrollbar-color: #605c5c #ececec; box-sizing: border-box; }

::-webkit-scrollbar { width: 0.675rem; height: 0.675rem; }

::-webkit-scrollbar-thumb { background: #605c5c; }

::-webkit-scrollbar-track { background: #ececec; }

pre { font-family: monospace, sans-serif; font-weight: 600; font-size: 0.75rem; font-size: clamp(0.75rem, 0.725rem + 0.125vw, 0.875rem); line-height: normal; max-height: 32rem; margin: 0; overflow: auto; padding-right: 0.375rem; }

pre::-webkit-scrollbar { width: 0.375rem; height: 0.375rem; }

pre::-webkit-scrollbar-thumb { border-radius: 1rem; }

pre::-webkit-scrollbar-track { border-radius: 1rem; }

.modal-dialog { margin: 4rem auto !important; max-width: 66.67%; }

.modal-header { padding: 0.75rem 0.75rem !important; border-top-left-radius: 0.375rem !important; border-top-right-radius: 0.375rem !important; }

.modal-header > .btn-danger { font-size: 1.125rem !important; padding: 0.325rem 0.625rem !important; }

.modal-content { border-radius: 0.375rem !important; border: 0 !important; }

.modal-body { padding: 0.75rem !important; max-height: 36rem !important; }
